'use strict'
const moment = require('moment')

const consoleColors = {
  cyanBgWhiteTx_blankBgCyanTx: '\x1b[46m\x1b[30m %s \x1b[0m\x1b[0m\x1b[36m %s \x1b[0m',
  yellowBgWhiteTx_blankBgYellowTx: '\x1b[43m\x1b[30m %s \x1b[0m\x1b[0m\x1b[33m %s \x1b[0m',
  magentaBgWhiteTx_blankBgMagentaTx: '\x1b[45m\x1b[30m %s \x1b[0m\x1b[0m\x1b[35m %s \x1b[0m',
  redBgWhiteTx_blankBgRedTx: '\x1b[41m\x1b[30m %s \x1b[0m\x1b[0m\x1b[31m %s \x1b[0m'
}

const __logger = (descr = '', color = 'cyanBgWhiteTx_blankBgCyanTx') => {
  console.info(consoleColors[color] ?
               consoleColors[color] :
               '', descr, moment().format('DD/MM/YYYY hh:mm:ss a'));
}

const isDev = () => {
  return ['development', 'dev'].indexOf(process.env.NODE_ENV.toLowerCase()) !== -1
}

const isProd = () => {
  return ['production', 'prod'].indexOf(process.env.NODE_ENV.toLowerCase()) !== -1
}

module.exports = {
  __logger,
  isDev,
  isProd
}
